DECLARE
    @FechaProceso DATE
    ,@DiasCargar INT

SELECT @FechaProceso = Valor FROM dbo.Parametros WHERE Clave = 'FechaProceso';
SELECT @DiasCargar = Valor FROM dbo.Parametros WHERE Clave = 'DiasCargarBorrar';

SELECT Codigo, Clave, (CONVERT(VARCHAR, DATEADD(DAY, @DiasCargar, @FechaProceso), 23)) AS Valor FROM dbo.Parametros WHERE Clave = 'FechaProceso';

DECLARE
	@FechaInicio DATE
	,@FechaFin DATE
	,@DiasBorrarCargar INT
	,@Contenedor VARCHAR(5)
	,@MedallaBronce VARCHAR(25)

SELECT @FechaFin = MAX(Valor) FROM dbo.Parametros WHERE Clave = 'FechaProceso';
SELECT @DiasBorrarCargar = (MAX(Valor) * -1) FROM dbo.Parametros WHERE Clave = 'DiasCargarBorrar';
SELECT TOP 1 @Contenedor = Valor FROM dbo.Parametros WHERE Clave = 'ContenedorLagoDatosReg' ORDER BY Codigo ASC;
SELECT TOP 1 @MedallaBronce = Valor FROM dbo.Parametros WHERE Clave = 'MedallaBronce' ORDER BY Codigo ASC;
SET @FechaInicio = DATEADD(DAY, @DiasBorrarCargar, @FechaFin)

DECLARE @TablaTemporal TABLE (FechaProceso DATE, Contenedor VARCHAR(5), Medalla VARCHAR(25));

WHILE @FechaInicio <= @FechaFin 
BEGIN 
	
	INSERT INTO @TablaTemporal 
	VALUES (@FechaInicio, @Contenedor, @MedallaBronce);

	SET @FechaInicio = DATEADD(DAY, 1, @FechaInicio);

END 

SELECT FechaProceso, Contenedor, Medalla FROM @TablaTemporal;

